package com.example.jetpackcomposeassignment.common.composableimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.widthimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.clipimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.layout.ContentScaleimport androidx.compose.ui.res.stringResourceimport androidx.compose.ui.unit.Dpimport androidx.compose.ui.unit.dpimport coil.compose.AsyncImageimport com.example.jetpackcomposeassignment.R@Composablefun CoilImageComponent(    imageUrl: String,    width: Dp = 104.dp,    boxModifier: Modifier = Modifier        .clip(shape = RoundedCornerShape(8.dp))        .background(Color.Gray),    modifier: Modifier = Modifier        .height((width.value * 1.36).dp),    contentScale: ContentScale = ContentScale.Crop) {    Box(        modifier = boxModifier    ) {        AsyncImage(            model = imageUrl,            contentDescription = stringResource(id = R.string.cat_image_accessibility),            contentScale = contentScale,            modifier = modifier,        )    }}