package com.example.jetpackcomposeassignment.common.utilimport java.time.LocalDateTimeimport java.time.format.DateTimeFormatterfun toFormattedDate(date: String): String {    val dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd'T'HH:mm:ss'Z'")    val formattedDate = LocalDateTime.parse(date, dateFormatter)    return DateTimeFormatter.ofPattern("MMMM dd, yyyy").format(formattedDate)}