package com.example.jetpackcomposeassignment.common.utilimport com.example.jetpackcomposeassignment.Rimport com.example.jetpackcomposeassignment.common.UiTextimport retrofit2.HttpExceptionfun HttpException.toErrorMessage(): UiText {    return if(localizedMessage.isNullOrEmpty()) {        UiText.Resource(R.string.an_unexpected_error_occured)    } else {        UiText.Dynamic(localizedMessage)    }}