package com.example.jetpackcomposeassignment.feature_listcat.data.repository_remoteimport com.example.jetpackcomposeassignment.feature_listcat.data.model.Catimport retrofit2.http.GETimport retrofit2.http.Queryinterface CatApi {    @GET("cats")    suspend fun getCats(        @Query("tags") tags: String = "cute",        @Query("limit") limit: String = "20",    ): List<Cat>}