package com.example.jetpackcomposeassignment.feature_listcat.presentation.cats.composableimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.widthimport androidx.compose.material3.Dividerimport androidx.compose.material3.Surfaceimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.text.style.TextOverflowimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport com.example.jetpackcomposeassignment.common.CatUrlimport com.example.jetpackcomposeassignment.common.composable.CoilImageComponentimport com.example.jetpackcomposeassignment.feature_listcat.data.model.Cat@Composablefun CatListItem(    cat: Cat,    onItemClick: (Cat) -> Unit,) {    Surface(        modifier = Modifier            .clickable(                onClick = {                    onItemClick(cat)                })            .padding(horizontal = 8.dp)    ) {        Column {            Spacer(modifier = Modifier.height(8.dp))            CoilImageComponent(                imageUrl = "${CatUrl.PICTURE}${cat.id}"            )            Spacer(modifier = Modifier.width(8.dp).height(8.dp))        }    }}