package com.example.jetpackcomposeassignment.feature_listcat.utilimport com.example.jetpackcomposeassignment.Rimport com.example.jetpackcomposeassignment.common.UiTextfun getIOExceptionMessage() = UiText.Resource(R.string.check_your_internet_connection)fun getHttpExceptionMessage(    message: String?) = when {    message != null -> UiText.Dynamic(message)    else -> UiText.Resource(R.string.an_unexpected_error_occured)}